import { assert } from "chai";
import { describe, it } from "mocha";
// Utils
import { baseHttp, withAuthHttp } from "../axios";
import HttpStatusCode from "../../utils/httpStatusCode";
// Types
import { AxiosError } from "axios";
import { ERROR_MSG, Product } from "../../components/product/product.entity";
 
// const products = async () => (await baseHttp.get('/products')).data as Product[]

const loginData1 = {
    phone: "01144551730",
    password: "P@ssword1",
}

const loginData2 = {
    phone: "01144551723",
    password: "P@ssword1",
}

describe('GET /products', async () => {
    it('Responds with the right JSON body', async () => {
        const req = await baseHttp.get('products');
        assert.strictEqual(req.status, HttpStatusCode.OK);
        const products: Product[] = req.data;
        assert.isArray(products);
        assert.isDefined(products[0].id)
        assert.isDefined(products[0].title)
        assert.isDefined(products[0].price)
        assert.isDefined(products[0].image)
    })
})

describe('GET /product/:id', async () => {
    let productId: string;
    before(async () => {
        const req = await baseHttp.get('products');
        productId = req.data[0].id;
    })
    it('Responds with the right JSON body', async () => {
        const req = await baseHttp.get(`product/${productId}`);
        
        assert.strictEqual(req.status, HttpStatusCode.OK);
        const product: Product = req.data;

        assert.isDefined(product.id)
        assert.isDefined(product.title)
        assert.isDefined(product.price)
        assert.isDefined(product.image)
    })

    it('gets 404 with nonExisting UUID', async () => {
        try {
            const corruptedId = productId.replace(productId[2], 'a');
            await baseHttp.get(`product/${corruptedId}`);
        } catch(error) {
            if(error instanceof AxiosError) {
                assert.strictEqual(error.response!.status, HttpStatusCode.NOT_FOUND);
        
                assert.isString(error.response!.data.message)
                assert.equal(error.response!.data.message, ERROR_MSG.NOT_FOUND)    
                return;
            }
            throw error;
        }

    })

    it('gets 400 with wrong :id format', async () => {
        try {
            await baseHttp.get(`product/22`);
        } catch(error) {
            if(error instanceof AxiosError) {
                assert.strictEqual(error.response!.status, HttpStatusCode.BAD_REQUEST);
        
                assert.isString(error.response!.data.message)
                assert.equal(error.response!.data.message, ERROR_MSG.NOT_FOUND)    
                return;
            }
            throw error;
        }

    })
})
  

describe('POST /product', () => {
    const data =  {
        title: "E2E Seller",
        price: 1212,
        image: ""
    };
    let token: string
    before(async () => {
        const req = await baseHttp.post('user/login', loginData1);
        token = req.data.accessToken;
    })

    it('it post valid data correctly', async () => {
        const req = await withAuthHttp(token).post('/product', data);

        assert.containsAllKeys(req.data, data);
        
        await withAuthHttp(token).delete(`product/${req.data.id}`)
    })

    it('returns the correct error message with invalid data', async () => {
            
        await withAuthHttp(token).post('/product', {
            // title: "E2E Seller",
            price: 1212 ,
            image: ""
        })
        .catch(error => {
            if(error instanceof AxiosError) {
                assert.equal(error.response!.status, HttpStatusCode.BAD_REQUEST);
                assert.equal(error.response!.data.message, ERROR_MSG.TITLE);
                return;
            }
            throw error;
        })

        await withAuthHttp(token).post('/product', {
        title: "E2E Seller",
        // price: 1212 ,
        image: ""
        })
        .catch(error => {
            if(error instanceof AxiosError) {
                assert.equal(error.response!.status, HttpStatusCode.BAD_REQUEST);
                assert.equal(error.response!.data.message, ERROR_MSG.PRICE);
                return;
            }
            throw error;
        })
  

        await withAuthHttp(token).post('/product', {
            title: "E2E Seller",
            price: 1212 ,
            image: "assaffas"
        }).catch(error => {
            if(error instanceof AxiosError) {
                assert.equal(error.response!.status, HttpStatusCode.BAD_REQUEST);
                assert.equal(error.response!.data.message, ERROR_MSG.IMAGE);
                return;
            }
            throw error;
        })

        await withAuthHttp("1221").post('/product', {
            title: "E2E Seller",
            price: 1212 ,
            image: ""
        }).catch(error => {
            if(error instanceof AxiosError) {
                assert.equal(error.response!.status, HttpStatusCode.UNAUTHORIZED);
                assert.equal(error.response!.data.message,"Unautorized for this request",);
                return;
            }
            throw error;
        })

        await baseHttp.post('/product', {
            title: "E2E Seller",
            price: 1212 ,
            image: ""
        }).catch(error => {
            if(error instanceof AxiosError) {
                assert.equal(error.response!.status, HttpStatusCode.UNAUTHORIZED);
                assert.equal(error.response!.data.message,"Unautorized for this request",);
                return;
            }
            throw error;
        })
    })
})
